// Custom Properties
:root {
    --dark-bg-color: #1F2022;
    --light-bg-color: rgb(153, 151, 151);
    --light-accent-color: white;
    --dark-accent-color: black;
    --transition-default: ease 0.4s;
    --font-Helvetica: 'Helvetica', serif;
    --light-transparent-bg-color: rgba(255, 255, 255, 0.3);
    --dark-transparent-bg-color: rgba(20, 20, 20, 0.7);
}

// General
body {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    background-color: var(--light-accent-color);
}

h1, h2, h3, h4, p {
    font-family: 'Helvetica', serif;
}

// Header --Section
@mixin headerLogoSize {
    padding: 0em 1em;
    width: 100%;
    max-width: 6em;
    max-height: 6em;
  };

.desktop-header {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    display: flex;
    justify-content: space-between;
    z-index: 3;
    background-color: var(--light-transparent-bg-color);
    transition: var(--transition-default);
    width: 98vw;
    padding-left: 1em;
    padding-right: 1em;
    @media only screen and (max-width: 900px) {
        top: 0;
        bottom: 0;
        left: -90%;
        width: 70%;
        flex-direction: column;
        justify-content: flex-start;
        background-color: var(--dark-bg-color);
        border-right: solid 1px var(--light-accent-color);
    }
    .desktop-header-left {
        display: flex;
        @media only screen and (max-width: 900px) {
            flex-direction: column;
            padding-bottom: 3em;
            border-bottom: 2px solid var(--light-accent-color);
        }
        .headerLogoLink {
            width: auto;
            height: 100%;
            margin: 0 auto;
            display: flex;
        }
        img {
            @include headerLogoSize;
            @media only screen and (max-width: 1300px) {
                transform: scale(0.8);
            }
            @media only screen and (max-width: 900px) {
                flex-direction: column;
                margin: 0 auto;
                transform: scale(1);
            }
        }
        h1 {
            margin: 0;
            align-self: center;
            color: var(--light-accent-color);
            @media only screen and (max-width: 1300px) {
                font-size: 1.5em
            }
            @media only screen and (max-width: 950px) {
                font-size: auto;
            }
        }
    }
    .desktop-header-right {
        display: flex;
        overflow: initial;
        padding-right: 1.2em;
        @media only screen and (max-width: 900px) {
            justify-content: center;
            margin: 0 auto;
        }
        .headerMenuItems {
            display: flex;
            justify-content: space-around;
            align-self: center;
            @media only screen and (max-width: 900px) {
                justify-content: center;
                flex-direction: column;
                margin-left: -2.5em;
            }
            .navItem {
                cursor: pointer;
                display: flex;
                margin-left: 2em;
                margin-right: 1em;
                font-size: 1.2em;
                font-family: var(--font-Helvetica);
                font-weight: bolder;
                color: var(--light-accent-color);
                text-decoration: none;
                &:hover {
                    text-decoration: underline;
                }
                &:focus {
