    // Images and Text Panel --Section
    @mixin numberOfPanels {
        grid-template-columns: repeat(4, 1fr);
        @media only screen and (max-width: 1200px) {
            grid-template-columns: repeat(2, 1fr);
        }
        @media only screen and (max-width: 700px) {
            grid-template-columns: repeat(1, 1fr);
        }
      };

.ImagesTextPanel-Section {
    height: 100%;
    width: 100%;
    .imageTextPanelHeading {
        width: 100%;
        margin: 2em 0 2em 0;
        text-align: center;
        font-weight: lighter;
    }
    .panelContainer {
        display: grid;
        grid-gap: 1em;
        padding: 1em;
        max-width: 100em;
        width: 80%;
        justify-items: center;
        margin: 0 auto;
        @include numberOfPanels;
        .imageTextPanelLink {
            height: 100%;
            width: 100%;
        }
        .ImagesTextPanelDivs {
            height: 25em;
            position: relative;
            width: 100%;
            transition: var(--transition-default);
            &:hover {
                filter: brightness(150%);
                box-shadow: 2px 2px 4px 1px var(--dark-accent-color);
                border-radius: 5px;
            }
            &:hover .imageTextPanelText {
                color: var(--dark-accent-color);
                text-shadow: 0.00001em 0.00001em 0.5em var(--light-accent-color);
                left: 7%;
                bottom: 6%;
            }
            &:hover .imageTextPanelText::after {
                width: 100%;
            }
            .imageTextPanelText {
                position: absolute;
                bottom: 5%;
                left: 5%;
                transition: var(--transition-default);
                color: var(--light-accent-color);
                &::after {
                    content: '';
                    height: 2px;
                    background-color: var(--light-accent-color);
                    position: absolute;
                    margin: 0 auto;
                    left: 0;
                    bottom: 0;
                    width: 0%;
                    transition: var(--transition-default);
                }
            }
        }
    }
  }

